mapper.select:
SELECT * FROM histories
WHERE user_id = #{userId}
ORDER BY created_at DESC

mapper.update:
UPDATE histories
SET course_id = #{courseId}
WHERE history_id = #{historyId}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END
FROM histories
WHERE user_id = #{userId}
  AND group_id = #{groupId}
